package twokseventeen

class Day1 {
    companion object {
        @JvmStatic
        fun main(args: Array<String>) {
            System.out.println("Day 1. Captcha 1")
            System.out.println("Captcha 1122: ${solveCaptcha("1122")}")
            System.out.println("Captcha 1111: ${solveCaptcha("1111")}")
            System.out.println("Captcha 1234: ${solveCaptcha("1234")}")
            System.out.println("Captcha 91212129: ${solveCaptcha("91212129")}")
            System.out.println(
                    "Captcha : ${solveCaptcha(
                            "")}")

            System.out.println("Day 2. Captcha 2")
            System.out.println("Captcha 1212: ${solveCaptcha2("1212")}")
            System.out.println("Captcha 1221: ${solveCaptcha2("1221")}")
            System.out.println("Captcha 123425: ${solveCaptcha2("123425")}")
            System.out.println("Captcha 123123: ${solveCaptcha2("123123")}")
            System.out.println("Captcha 12131415: ${solveCaptcha2("12131415")}")
            System.out.println(
                    "Captcha : ${solveCaptcha2(
                            "")}")
        }

        fun solveCaptcha(captcha: String): String {
            val chars = captcha.toCharArray()
            val result = mutableListOf<Char>()
            for (i in 1 until chars.size) {
                if (chars[i - 1] == chars[i]) {
                    result.add(chars[i])
                }
            }
            if (chars.last() == chars[0]) {
                result.add(chars[0])
            }
            return result.map { it.toString().toInt() }.sum().toString()
        }

        fun solveCaptcha2(captcha: String): String {
            val chars = captcha.toCharArray()
            val result = mutableListOf<Char>()
            val step = chars.size / 2

            for (i in step until chars.size){
                if (chars[i - step] == chars[i]) {
                    result.add(chars[i])
                    result.add(chars[i])
                }
            }
            return result.map { it.toString().toInt() }.sum().toString()
        }
    }
}